import React, {Component} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import bgimg from '../img/OMK.svg'
import Logo from '../parts/logo.jsx'
class MainPage extends Component {

    constructor(props) {
        super(props)
        this.state = { screenWidth: ""}
      }

    componentDidMount() {
        window.addEventListener("resize", this.resize.bind(this));
        this.resize();
    }

    resize() {
        if(window.innerWidth < 400) {
            this.setState("0px")
        }
        this.setState("auto 95%")
}

    render() {
        return (
            <div style={bgimgStyle} className="container-fluid">
                <p>{}</p>
                <Logo />
            </div>
        )
    }}
const bgimgStyle = {
    background: 'url('+ bgimg +') no-repeat right fixed',
    backgroundSize: this.state.screenWidth,
    width: '100%',
    height: '100%',
}
    export default MainPage
