import React, {Component} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import bgimg from '../img/error/gargantuabg.svg'
import deadman from '../img/error/deadman.svg'
import junk from '../img/error/junk.svg'
import junks from '../img/error/junks2.svg'
import Logo from '../parts/logo.jsx'

window.onload = () => {
    animate({
        duration: 1000,
        timing: (timeFraction) => Math.pow(timeFraction, 2),
        draw: (progress) => imgStyleD.right = progress * 1000 + 'px'
    })
}

function animate(options) {
    var start = performance.now()
    requestAnimationFrame(function anim(time) {
        var timeFraction = (time - start) / options.duration
        if (timeFraction > 1) timeFraction = 1
        var progress = options.timing(timeFraction)
        options.draw(progress)
        if (timeFraction < 1) {requestAnimationFrame(anim)}
    })
}

class ErrorPage extends Component {
    render() {
        return (
            <div style={bgimgStyle} className="container-fluid">
                <div className="row">
                    
                    <div className="col-12 col-md-10 col-xl-7 position-absolute fixed-bottom" id='members deadman'> <img src={deadman} alt="man" style={imgStyleD}/> </div>
                    <div className="col-12 col-md-10 col-xl-7 position-absolute fixed-bottom" id='members'> <img src={junk} alt="junk" style={imgStyle}/> </div>
                    <div className="col-12 col-md-10 col-xl-7 position-absolute fixed-bottom" id='members'> <img src={junks} alt="junks" style={imgStyle}/> </div>
                    <div className="col-xl-7 col-md-6 fixed-top" style={message}>
                        <div className="row"> <h3>Вітаємо, ви дійшли до кінця Інтернету</h3></div>
                        <div className="row"> <h6>Пройшовши довгий шлях в пошуках вашого запиту ви нічого не знайшли. Радимо вам повернутися на <span style={{cursor:"pointer",textDecoration:"underline"}}>початок</span> ваших пригод.</h6></div>
                    </div>
                </div>
                <Logo />
            </div>
        )
    }}
const bgimgStyle = {
    background: 'url('+ bgimg + ') no-repeat center top fixed',
    backgroundSize: 'cover',
    boxShadow: 'inset 0 0 50px #000',
    width: '100%',
    height: '100%'
}
const message = {
    color: 'white',
    margin: '50px',
}
const imgStyle = {
    width: '100%',
    padding: '10px',
    paddingLeft: '70px'
}
const imgStyleD = {
    width: '100%',
    padding: '40px',
    paddingLeft: '100px',
    right: '40px',
}
    export default ErrorPage
