import React, {Component} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import bgimg from '../img/error/gargantuabg.svg'
import deadman from '../img/error/deadman.png'
import junk from '../img/error/junk.svg'
import junks from '../img/error/junks2.svg'
import Logo from '../parts/logo.jsx'
class ErrorPage extends Component {
    constructor (props) {
        super(props);
        this.state = {
            move: 10
        }
    }
    start = Date.now()
    componentDidMount() {
        this.stateID = setInterval(() => {
            this.timePassed = Date.now() - this.start
            if(this.state.move >= 1000) {
                clearInterval(this.stateID)
                return
            }
            this.animate(this.timePassed)}, 10)
    }
    componentWillUnmount(){
        clearInterval(this.stateID)
    }
    animate(timePassed) {
            this.setState((state)=>({move: state.move = timePassed / 2}))
    }
    render() {
        return (
            <div style={bgimgStyle} className="container-fluid">
                <div className="row">
                    <div className="col-10 col-sm-8 col-md-6 col-xl-7 offset-1 fixed-top" style={message}>
                        <div className="row"> <h3>Вітаємо, ви дійшли до кінця Інтернету</h3></div>
                        <div className="row"> <h6>Пройшовши довгий шлях в пошуках вашого запиту ви нічого не знайшли. Радимо вам повернутися на <span style={{cursor:"pointer",textDecoration:"underline"}}>початок</span> ваших пригод.</h6></div>
                    </div>
                    <img src={junks} alt="junks" id="parts" style={imgStyle} />
                    <div style={{marginLeft:this.state.move+'px', width: '60%', position: 'absolute',overflow: 'hidden'}}><img src={deadman} alt="man" id="parts" style={{width:'100%',overflow: 'hidden'}}/></div>
                    <img src={junk} alt="junk" id="parts" style={imgStyle}/>
                </div>
                <Logo />
            </div>
        )
    }}
const bgimgStyle = {
    background: 'url('+ bgimg + ') no-repeat center top fixed',
    backgroundSize: 'cover',
    boxShadow: 'inset 0 0 50px #000',
    width: '100%',
    height: '100%'
}
const message = {
    color: 'white',
    paddingTop: '50px',
}
const imgStyle = {
    width: '60%',
    position: 'absolute',
    bottom: '0',
    padding: '10px',
    paddingLeft: '70px',

}
    export default ErrorPage
